mapper.select:
SELECT FTC_FOLIO AS FOLIO, FTN_ID_PRODUCTO AS ID, FTN_CANTIDAD AS CANTIDAD, FTN_ID_TIPO_UNIDAD AS TIPO_UNIDAD, CADUCIDAD_PRODUCTO AS CADUCIDAD, COSTO_UNITARIO AS COSTO_UNITARIO, COSTO_TOTAL AS COSTO_TOTAL, FTN_ID_PRECIO AS ID_PRECIO, FTN_TIPO_MOV AS TIPO_MOV, FCB_VIGENCIA AS VIGENCIA, FCD_FEH_APROB AS FECHA_APROBACION, FCC_USU_APROB AS USUARIO_APROBACION, FCD_FEH_CRE AS FECHA_CREACION, FCC_USU_CRE AS USUARIO_CREACION, FCD_FEH_ACT AS FECHA_ACTUALIZACION, FCC_USU_ACT AS USUARIO_ACTUALIZACION FROM TT_FARM_INVENTARIO
AND FTC_FOLIO = #{numeroFolio}
AND FTN_ID_PRODUCTO = #{idProducto}
AND TRUNC(CADUCIDAD_PRODUCTO) = TRUNC(#{caducidadProducto})
ORDER BY CADUCIDAD_PRODUCTO

mapper.update:
UPDATE TT_FARM_INVENTARIO SET FTN_CANTIDAD = #{cantidad}, FTN_ID_TIPO_UNIDAD = #{idTipoUnidad}, CADUCIDAD_PRODUCTO = #{caducidadProducto}, COSTO_UNITARIO = #{costoUnitario}, COSTO_TOTAL = #{costoTotal}, FTN_ID_PRECIO = #{idPrecio}, FTN_TIPO_MOV = #{tipoMovimiento}, FCB_VIGENCIA = #{vigencia}, FCD_FEH_ACT = SYSTIMESTAMP, FCC_USU_ACT = #{usuarioActualizacion}
AND FTC_FOLIO = #{numeroFolio}
AND FTN_ID_PRODUCTO = #{idProducto}
