mapper.select:
SELECT VENT.FTC_FOLIO AS FOLIO, VENT.FTN_ID_PRODUCTO AS ID, INV.FCC_VALOR AS DESC_PRODUCTO, VENT.FTN_CANTIDAD AS CANTIDAD, VENT.FTN_ID_TIPO_UNIDAD AS TIPO_UNIDAD, INV.FCC_DESC AS DESC_TIPO, VENT.FTN_ID_PRECIO AS ID_PRECIO, VENT.PRECIO_TOTAL AS COSTO_TOTAL, VENT.FCB_VIGENCIA AS VIGENCIA, VENT.FCD_FEH_CRE AS FECHA_CREACION, VENT.FCC_USU_CRE AS USUARIO_CREACION, VENT.FCD_FEH_ACT AS FECHA_ACTUALIZACION, VENT.FCC_USU_ACT AS USUARIO_ACTUALIZACION FROM TT_FARM_VENTAS VENT INNER JOIN CAT_CATALOGO INV ON INV.FCN_ID_CAT_CATALOGO = VENT.FTN_ID_PRODUCTO
AND VENT.FTC_FOLIO = #{numeroFolio}
AND VENT.FTN_ID_PRODUCTO = #{idProducto}
AND VENT.FCB_VIGENCIA = #{vigencia}
ORDER BY VENT.FTC_FOLIO

mapper.update:

		UPDATE TT_FARM_INVENTARIO
    SET
    FTN_CANTIDAD = FTN_CANTIDAD - #{cantidad}
    WHERE FTN_ID_PRODUCTO = #{idProducto}
